const repoApp=new Yox({el:"#repo-root",template:"#repo-template",data:{loading:!0,github_repos:[]},async afterCreate(){const t=document.getElementById("github-username").textContent,e=await fetch(`https://api.github.com/users/${t}/repos?sort=updated&per_page=100`),o=await e.json();document.getElementById("repo-count").textContent=o.length<50?o.length:`at least ${o.length}`,this.set({loading:!1,github_repos:o})},computed:{repos_sorted(){return this.get("github_repos").sort((t,e)=>{let o=t.stargazers_count,r=e.stargazers_count;return t.fork&&(o*=.5),e.fork&&(r*=.5),r-o})}}});