const searchApp=new Yox({el:"#search-view-container",template:"#search-teamplate",data:{posts:[],keyword:""},async afterCreate(){const t=document.getElementById("search-teamplate").getAttribute("data-path"),e=await fetch(t),s=await e.json();this.set("posts",s.posts)},computed:{searchPosts(){const t=this.get("posts"),e=this.get("keyword");return t.filter(t=>t.title.includes(e)||t.text.includes(e)).map(t=>{const e={...t};return e.text=`${t.text.substring(0,100)}...`,e})}}});